import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.map.js";
import "core-js/modules/es.iterator.reduce.js";
import "core-js/modules/es.iterator.some.js";
import { ref, onMounted, computed, watch, onUnmounted, nextTick } from 'vue';
import { useStore } from 'vuex';
import Chart from 'chart.js/auto';
import axios from 'axios';
import moment from 'moment';

// Create axios instance with default config
const api = axios.create({
  baseURL: 'http://localhost:8000/api'
});
export default {
  name: 'Dashboard',
  setup() {
    const store = useStore();
    const performanceChart = ref(null);
    const assessmentTypeChart = ref(null);
    const performanceTrendChart = ref(null);
    const sectionChart = ref(null);
    const selectedYear = ref('');
    const selectedSection = ref('');
    const selectedSubject = ref('');
    const dashboardContainer = ref(null);
    const isLoading = ref(false);
    const lastUpdate = ref(null);
    const autoRefreshInterval = ref(null);
    const sections = ref([]);
    const subjects = ref([]);

    // Computed properties
    const isCITHead = computed(() => store.getters.isCITHead);
    const token = computed(() => store.state.auth.token);

    // Get user name
    const userName = computed(() => {
      const user = store.state.auth.user;
      return user ? user.name || `${user.firstName || ''} ${user.lastName || ''}`.trim() || 'User' : 'User';
    });

    // Check if filters are active
    const hasActiveFilters = computed(() => {
      return Boolean(selectedYear.value || selectedSection.value || selectedSubject.value);
    });

    // Set auth token for all requests
    watch(token, newToken => {
      if (newToken) {
        api.defaults.headers.common['Authorization'] = `Bearer ${newToken}`;
      }
    }, {
      immediate: true
    });

    // Dashboard stats
    const totalStudents = ref(0);
    const totalTeachers = ref(0);
    const averageAttendance = ref(0);
    const averageScore = ref(0);
    const activeSections = ref(0);
    const data = ref(null);

    // Computed properties for data availability
    const hasAttendanceData = computed(() => {
      if (data.value && data.value.attendanceDistribution) {
        return data.value.attendanceDistribution.some(val => val > 0);
      }
      return false;
    });
    const hasScoreData = computed(() => {
      return data.value?.averageScore > 0;
    });
    const hasPerformanceData = computed(() => {
      // Check if we have any assessment or performance data
      if (data.value) {
        // Check for averageScore > 0
        if (typeof data.value.averageScore === 'number' && data.value.averageScore > 0) {
          return true;
        }

        // Check for performance trends
        if (Array.isArray(data.value.performanceTrends) && data.value.performanceTrends.length > 0) {
          return true;
        }
      }
      return false;
    });
    const hasAssessmentData = computed(() => {
      if (data.value) {
        // Check directly in assessmentTypes
        if (data.value.assessmentTypes) {
          const assessmentTypes = Object.values(data.value.assessmentTypes);
          return assessmentTypes.some(type => type.count > 0);
        }

        // Fallback to assessmentTypeDistribution
        if (data.value.assessmentTypeDistribution) {
          return true;
        }
      }
      return false;
    });
    const hasSectionData = computed(() => {
      return data.value?.sections?.length > 0;
    });

    // Fetch sections and subjects
    const fetchSectionsAndSubjects = async (year = '') => {
      try {
        const response = await api.get('/department/sections-subjects', {
          params: {
            year
          }
        });
        if (response.data) {
          // Extract unique sections and subjects
          sections.value = response.data.sections || [];
          subjects.value = response.data.subjects || [];
        }
      } catch (error) {
        console.error('Error fetching sections and subjects:', error);
        sections.value = [];
        subjects.value = [];
      }
    };
    const fetchDashboardData = async () => {
      try {
        isLoading.value = true;
        const response = await api.get('/dashboard/stats', {
          params: {
            year: selectedYear.value,
            section: selectedSection.value,
            subject: selectedSubject.value
          }
        });
        data.value = response.data;

        // Update stats
        totalStudents.value = data.value.totalStudents || 0;
        totalTeachers.value = data.value.totalTeachers || 0;
        averageAttendance.value = data.value.averageAttendance || 0;
        averageScore.value = data.value.averageScore || 0;
        activeSections.value = data.value.activeSections || 0;

        // Update charts with new data
        if (data.value) {
          updateCharts(data.value);
        }
        lastUpdate.value = new Date();
      } catch (error) {
        console.error('Failed to fetch dashboard data:', error);
        if (error.response) {
          console.error('Error response:', error.response.data);
        }
      } finally {
        isLoading.value = false;
      }
    };
    const updateCharts = data => {
      updatePerformanceChart(data);
      updateAssessmentTypeChart(data);
      updatePerformanceTrendChart(data);
      updateSectionChart(data);
    };

    // Initialize dummy data for charts when no data is available
    const initDummyData = () => {
      updatePerformanceChart({
        attendanceDistribution: [0, 0, 0]
      });
      updateAssessmentTypeChart({
        assessmentData: [{
          type: 'Quiz',
          averageScore: 0
        }, {
          type: 'Activity',
          averageScore: 0
        }, {
          type: 'Performance Task',
          averageScore: 0
        }]
      });
      updatePerformanceTrendChart([]);
      updateSectionChart({
        sections: []
      });
    };

    // Format date for display
    const formatDate = date => {
      if (!date) return '';
      return moment(date).format('MMMM D, YYYY h:mm A');
    };

    // Handle year change
    const handleYearChange = async () => {
      selectedSection.value = '';
      selectedSubject.value = '';
      if (selectedYear.value) {
        await fetchSectionsAndSubjects(selectedYear.value);
      } else {
        sections.value = [];
        subjects.value = [];
      }
      await fetchDashboardData();
    };

    // Handle filter change
    const handleFilterChange = () => {
      fetchDashboardData();
    };

    // Clear all filters
    const clearFilters = () => {
      selectedYear.value = '';
      selectedSection.value = '';
      selectedSubject.value = '';
      sections.value = [];
      subjects.value = [];
      fetchDashboardData();
    };

    // Get filter display text
    const getFilterDisplay = () => {
      const filters = [];
      if (selectedYear.value) filters.push(selectedYear.value + ' Year');
      if (selectedSection.value) filters.push(selectedSection.value);
      if (selectedSubject.value) filters.push(selectedSubject.value);
      return filters.length > 0 ? filters.join(' - ') : 'Filter View';
    };

    // Attendance Distribution Chart
    const updatePerformanceChart = data => {
      if (!performanceChart.value) return;
      const ctx = performanceChart.value.getContext('2d');
      if (!ctx) return;
      const existingChart = Chart.getChart(ctx);
      if (existingChart) existingChart.destroy();

      // Get attendance distribution or use default values
      const attendanceData = data.attendanceDistribution || [0, 0, 0];
      new Chart(ctx, {
        type: 'doughnut',
        data: {
          labels: ['Present', 'Late', 'Absent'],
          datasets: [{
            data: attendanceData,
            backgroundColor: ['rgba(52, 211, 153, 0.8)',
            // Green for Present
            'rgba(251, 191, 36, 0.8)',
            // Yellow for Late
            'rgba(239, 68, 68, 0.8)' // Red for Absent
            ],
            borderColor: ['rgb(16, 185, 129)', 'rgb(245, 158, 11)', 'rgb(220, 38, 38)'],
            borderWidth: 1
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          plugins: {
            legend: {
              position: 'bottom',
              labels: {
                font: {
                  size: 12
                },
                padding: 15
              }
            }
          },
          cutout: '60%',
          layout: {
            padding: 20
          }
        }
      });
    };

    // Assessment Type Chart
    const updateAssessmentTypeChart = data => {
      if (!assessmentTypeChart.value) return;
      const ctx = assessmentTypeChart.value.getContext('2d');
      if (!ctx) return;
      const existingChart = Chart.getChart(ctx);
      if (existingChart) existingChart.destroy();

      // Process incoming data to restructure it
      let assessmentData = {};
      const assessmentTypes = ['Quiz', 'Activity', 'Performance Task'];

      // Initialize default data structure if proper data isn't available
      if (!data.assessmentData || !Array.isArray(data.assessmentData) || data.assessmentData.length === 0) {
        // Sample data structure for fallback
        assessmentData = {
          assessmentTypes: assessmentTypes,
          averageScores: {},
          totalCounts: {}
        };
        assessmentTypes.forEach(type => {
          assessmentData.averageScores[type] = 0;
          assessmentData.totalCounts[type] = 0;
        });
      } else {
        // Process actual data
        assessmentData = {
          assessmentTypes: assessmentTypes,
          averageScores: {},
          totalCounts: {}
        };

        // Calculate average scores per assessment type
        assessmentTypes.forEach(type => {
          const typeAssessments = data.assessmentData.filter(a => a.type === type);
          const totalCount = typeAssessments.length;

          // Get the count from the first matching assessment if available
          const firstMatch = typeAssessments[0];
          assessmentData.totalCounts[type] = firstMatch && typeof firstMatch.count === 'number' ? firstMatch.count : totalCount;
          if (totalCount > 0) {
            // Calculate average score
            const totalScore = typeAssessments.reduce((sum, a) => sum + (parseFloat(a.averageScore) || 0), 0);
            assessmentData.averageScores[type] = (totalScore / totalCount).toFixed(1);
          } else {
            assessmentData.averageScores[type] = 0;
          }
        });
      }

      // Create a simple bar chart showing average scores
      new Chart(ctx, {
        type: 'bar',
        data: {
          labels: assessmentTypes,
          datasets: [{
            label: 'Average Score (%)',
            data: assessmentTypes.map(type => assessmentData.averageScores[type]),
            backgroundColor: ['rgba(52, 211, 153, 0.8)',
            // Green for Quiz
            'rgba(59, 130, 246, 0.8)',
            // Blue for Activity
            'rgba(251, 191, 36, 0.8)' // Yellow for Performance Task
            ],
            borderColor: ['rgb(15, 140, 80)', 'rgb(45, 110, 220)', 'rgb(220, 160, 20)'],
            borderWidth: 1,
            borderRadius: 6,
            barPercentage: 0.6
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          scales: {
            y: {
              beginAtZero: true,
              max: 100,
              grid: {
                color: 'rgba(0, 0, 0, 0.05)'
              },
              title: {
                display: true,
                text: 'Average Score (%)',
                font: {
                  size: 14,
                  weight: 'bold'
                }
              },
              ticks: {
                font: {
                  size: 12
                }
              }
            },
            x: {
              grid: {
                display: false
              }
            }
          },
          plugins: {
            legend: {
              display: false
            }
          }
        }
      });
    };

    // Performance Trend Chart
    const updatePerformanceTrendChart = data => {
      if (!performanceTrendChart.value) return;
      const ctx = performanceTrendChart.value.getContext('2d');
      if (!ctx) return;
      const existingChart = Chart.getChart(ctx);
      if (existingChart) existingChart.destroy();

      // Check and normalize data
      let performanceTrends = [];
      if (data.performanceTrends && Array.isArray(data.performanceTrends)) {
        performanceTrends = data.performanceTrends.slice(0, 10); // Take last 10 assessments
      }

      // Sort by date (oldest first for chronological order)
      performanceTrends.sort((a, b) => new Date(a.date) - new Date(b.date));

      // Format dates and handle missing data
      const labels = performanceTrends.map(item => {
        const date = new Date(item.date);
        return `${date.getMonth() + 1}/${date.getDate()}`;
      });
      const scoreData = performanceTrends.map(item => item.score);
      new Chart(ctx, {
        type: 'line',
        data: {
          labels: labels,
          datasets: [{
            label: 'Average Score (%)',
            data: scoreData,
            borderColor: 'rgb(59, 130, 246)',
            backgroundColor: 'rgba(59, 130, 246, 0.1)',
            tension: 0.3,
            fill: true,
            pointBackgroundColor: 'rgb(59, 130, 246)',
            pointBorderColor: '#fff',
            pointBorderWidth: 2,
            pointRadius: 4,
            pointHoverRadius: 6
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          scales: {
            y: {
              beginAtZero: true,
              max: 100,
              grid: {
                color: 'rgba(0, 0, 0, 0.05)'
              },
              title: {
                display: true,
                text: 'Score (%)',
                font: {
                  size: 14,
                  weight: 'bold'
                }
              }
            },
            x: {
              grid: {
                display: false
              }
            }
          },
          plugins: {
            legend: {
              position: 'top',
              align: 'end',
              labels: {
                boxWidth: 10,
                usePointStyle: true,
                pointStyle: 'circle'
              }
            },
            tooltip: {
              callbacks: {
                label: context => {
                  return `Score: ${context.parsed.y}%`;
                }
              }
            }
          }
        }
      });
    };

    // Section Performance Chart (Keep this chart from the original Dashboard)
    const updateSectionChart = data => {
      if (!sectionChart.value) return;
      const ctx = sectionChart.value.getContext('2d');
      if (!ctx) return;
      const existingChart = Chart.getChart(ctx);
      if (existingChart) existingChart.destroy();

      // Check if there is section data
      if (!data.sections || !Array.isArray(data.sections) || data.sections.length === 0) {
        // Draw empty chart with message
        new Chart(ctx, {
          type: 'bar',
          data: {
            labels: ['No Data'],
            datasets: [{
              data: [0],
              backgroundColor: 'rgba(229, 231, 235, 0.5)',
              borderColor: 'rgb(209, 213, 219)',
              borderWidth: 1
            }]
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            scales: {
              y: {
                beginAtZero: true,
                max: 100
              }
            },
            plugins: {
              legend: {
                display: false
              }
            }
          }
        });
        return;
      }

      // Process section data
      const labels = data.sections.map(section => section.name);
      const scoreData = data.sections.map(section => section.averageScore);
      const attendanceData = data.sections.map(section => section.attendanceRate);

      // Create multi-dataset bar chart
      new Chart(ctx, {
        type: 'bar',
        data: {
          labels: labels,
          datasets: [{
            label: 'Average Score',
            data: scoreData,
            backgroundColor: 'rgba(59, 130, 246, 0.6)',
            borderColor: 'rgb(37, 99, 235)',
            borderWidth: 1,
            borderRadius: 4,
            barPercentage: 0.6,
            categoryPercentage: 0.7
          }, {
            label: 'Attendance Rate',
            data: attendanceData,
            backgroundColor: 'rgba(52, 211, 153, 0.6)',
            borderColor: 'rgb(16, 185, 129)',
            borderWidth: 1,
            borderRadius: 4,
            barPercentage: 0.6,
            categoryPercentage: 0.7
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          scales: {
            y: {
              beginAtZero: true,
              max: 100,
              grid: {
                color: 'rgba(0, 0, 0, 0.05)'
              },
              title: {
                display: true,
                text: 'Percentage (%)',
                font: {
                  size: 14,
                  weight: 'bold'
                }
              }
            },
            x: {
              grid: {
                display: false
              }
            }
          },
          plugins: {
            legend: {
              position: 'top',
              align: 'end',
              labels: {
                boxWidth: 12,
                usePointStyle: true,
                pointStyle: 'circle'
              }
            },
            tooltip: {
              callbacks: {
                label: function (context) {
                  return `${context.dataset.label}: ${context.parsed.y}%`;
                }
              }
            }
          }
        }
      });
    };
    const refreshDashboard = () => {
      fetchDashboardData();
    };
    const scrollToSection = sectionId => {
      const section = document.getElementById(sectionId);
      if (section) {
        section.scrollIntoView({
          behavior: 'smooth'
        });
      }
    };

    // Auto-refresh logic
    const setupAutoRefresh = () => {
      // Clear any existing interval
      if (autoRefreshInterval.value) {
        clearInterval(autoRefreshInterval.value);
      }

      // Set new interval (every 5 minutes)
      autoRefreshInterval.value = setInterval(() => {
        fetchDashboardData();
      }, 5 * 60 * 1000);
    };

    // Fetch data on component mount
    onMounted(async () => {
      try {
        // Initialize with dummy data while loading
        nextTick(() => {
          initDummyData();
        });

        // Fetch sections and subjects
        if (selectedYear.value) {
          await fetchSectionsAndSubjects(selectedYear.value);
        }

        // Fetch dashboard data
        await fetchDashboardData();
        setupAutoRefresh();
      } catch (error) {
        console.error('Error initializing dashboard:', error);
      }
    });

    // Clean up on component unmount
    onUnmounted(() => {
      if (autoRefreshInterval.value) {
        clearInterval(autoRefreshInterval.value);
      }
    });
    return {
      performanceChart,
      assessmentTypeChart,
      performanceTrendChart,
      sectionChart,
      selectedYear,
      selectedSection,
      selectedSubject,
      sections,
      subjects,
      isCITHead,
      userName,
      hasActiveFilters,
      totalStudents,
      totalTeachers,
      averageAttendance,
      averageScore,
      activeSections,
      hasAttendanceData,
      hasScoreData,
      hasPerformanceData,
      hasAssessmentData,
      hasSectionData,
      dashboardContainer,
      isLoading,
      lastUpdate,
      formatDate,
      scrollToSection,
      handleYearChange,
      handleFilterChange,
      clearFilters,
      getFilterDisplay,
      refreshDashboard
    };
  }
};
