import { reactive } from 'vue'
import ToastNotification from '@/components/ToastNotification.vue'

export const toastState = reactive({
  toasts: [],
  timeouts: {}
})

export const toast = {
  success(message, duration = 3000, title = '') {
    this.show(message, 'success', duration, title)
  },
  
  error(message, duration = 3000, title = '') {
    this.show(message, 'error', duration, title)
  },
  
  warning(message, duration = 3000, title = '') {
    this.show(message, 'warning', duration, title)
  },
  
  info(message, duration = 3000, title = '') {
    this.show(message, 'info', duration, title)
  },
  
  show(message, type = 'info', duration = 3000, title = '') {
    const id = Date.now()
    
    toastState.toasts.push({
      id,
      message,
      type,
      title
    })
    
    if (duration > 0) {
      toastState.timeouts[id] = setTimeout(() => {
        this.remove(id)
      }, duration)
    }
    
    return id
  },
  
  remove(id) {
    const index = toastState.toasts.findIndex(toast => toast.id === id)
    if (index !== -1) {
      clearTimeout(toastState.timeouts[id])
      delete toastState.timeouts[id]
      toastState.toasts.splice(index, 1)
    }
  }
}

export default {
  install(app) {
    // Create a $toast property on the global instance
    app.config.globalProperties.$toast = toast
    
    // Register ToastNotification component globally
    app.component('ToastNotification', ToastNotification)
    
    // Provide toast as an injected property
    app.provide('toast', toast)
  }
}
