          selectedEndDate.value = selectedStartDate.value;
        }
        fetchDashboardData();
      }
    });
    onMounted(async () => {
      if (store.state.auth.user?._id && store.state.auth.token) {
        console.log('Component mounted, initializing...');

        // Wait for the next tick to ensure DOM elements are rendered
        await nextTick();
        try {
          // Initialize empty charts first
          console.log('Initializing empty charts...');
          initDummyData();
          console.log('Empty charts initialized');

          // Fetch sections and subjects
          await fetchTeacherSectionsAndSubjects(selectedYear.value);
          console.log('Sections and subjects fetched');

          // Fetch actual dashboard data
          await fetchDashboardData();
          console.log('Initial data fetch completed');
        } catch (error) {
          console.error('Error during initialization:', error);
        }
      } else {
        console.error('No user ID or token found');
      }
    });
    return {
      totalStudents,
      totalSections,
      totalSubjects,
      averageScore,
      performanceChart,
      recentActivities,
      sections,
      subjects,
      selectedYear,
      selectedSection,
      selectedSubject,
      hasAttendanceData,
      hasPerformanceData,
      hasAssessmentData,
      hasActivity,
      formatDate,
      getFilterDisplay,
      handleYearChange,
      applyFilters,
      assessmentTypeChart,
      performanceTrendChart,
      assessmentTypePerformanceChart,
      selectedStartDate,
      selectedEndDate,
      today
    };
  }
};
