      // Create datasets for each problem type
      const datasets = problemTypesArray.map((problemType, index) => {
        const colors = ['rgba(54, 162, 235, 0.7)',
        // Blue
        'rgba(255, 99, 132, 0.7)',
        // Red
        'rgba(255, 206, 86, 0.7)',
        // Yellow
        'rgba(75, 192, 192, 0.7)',
        // Green
        'rgba(153, 102, 255, 0.7)',
        // Purple
        'rgba(255, 159, 64, 0.7)' // Orange
        ];
        return {
          label: problemType,
          data: yearsArray.map(year => problemTypesByYear[year][problemType] || 0),
          backgroundColor: colors[index % colors.length]
        };
      });

      // Create the chart
      new Chart(ctx, {
        type: 'bar',
        data: {
          labels: yearsArray,
          datasets: datasets
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          scales: {
            x: {
              stacked: true
            },
            y: {
              stacked: true,
              beginAtZero: true,
              title: {
                display: true,
                text: 'Number of Problems'
              }
            }
          },
          plugins: {
            tooltip: {
              callbacks: {
                label: context => {
                  const value = context.raw || 0;
                  return `${context.dataset.label}: ${value} problems`;
                }
              }
            }
          }
        }
      });
    };

    // Create chart for problem severity distribution
    const createSeverityDistributionChart = () => {
      if (!severityDistributionChart.value) return;
      const ctx = severityDistributionChart.value.getContext('2d');
      if (!ctx) return;
      const existingChart = Chart.getChart(ctx);
      if (existingChart) existingChart.destroy();
      if (!surveyData.value || surveyData.value.length === 0) {
        // If no data, show empty chart
        new Chart(ctx, {
          type: 'doughnut',
          data: {
            labels: ['High', 'Medium', 'Low', 'Minimal'],
            datasets: [{
              data: [0, 0, 0, 0],
              backgroundColor: ['rgba(239, 68, 68, 0.8)',
              // Red (High)
              'rgba(251, 191, 36, 0.8)',
              // Yellow (Medium)
              'rgba(59, 130, 246, 0.8)',
              // Blue (Low)
              'rgba(52, 211, 153, 0.8)' // Green (Minimal)
              ]
            }]
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
              legend: {
                position: 'right'
              }
            }
          }
        });
        return;
      }

      // Process data - count problems by severity
      const severityCounts = {
        High: 0,
        Medium: 0,
        Low: 0,
        Minimal: 0
      };

      // Process all surveys and their stats
      surveyData.value.forEach(survey => {
        if (survey.surveyStats && Array.isArray(survey.surveyStats)) {
          survey.surveyStats.forEach(stat => {
            const average = stat.average || 0;
            if (average >= 7) {
              severityCounts.High++;
            } else if (average >= 5) {
              severityCounts.Medium++;
            } else if (average >= 3) {
              severityCounts.Low++;
            } else {
              severityCounts.Minimal++;
            }
          });
        }
      });

      // Create the chart
      new Chart(ctx, {
        type: 'doughnut',
        data: {
          labels: ['High', 'Medium', 'Low', 'Minimal'],
          datasets: [{
            data: [severityCounts.High, severityCounts.Medium, severityCounts.Low, severityCounts.Minimal],
            backgroundColor: ['rgba(239, 68, 68, 0.8)',
            // Red (High)
            'rgba(251, 191, 36, 0.8)',
            // Yellow (Medium)
            'rgba(59, 130, 246, 0.8)',
            // Blue (Low)
            'rgba(52, 211, 153, 0.8)' // Green (Minimal)
            ]
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          plugins: {
            legend: {
              position: 'right',
              labels: {
                boxWidth: 15,
                padding: 15
              }
            },
            tooltip: {
              callbacks: {
                label: context => {
                  const value = context.raw || 0;
                  const total = Object.values(severityCounts).reduce((sum, val) => sum + val, 0);
                  const percentage = total > 0 ? (value / total * 100).toFixed(1) : 0;
                  return `${context.label}: ${value} (${percentage}%)`;
                }
              }
            }
          }
        }
      });
    };
    const __returned__ = {
      store,
      surveyAverageChart,
      performanceChart,
      attendanceChart,
      assessmentTypeChart,
      performanceTrendChart,
      quizzesChart,
      assessmentTypePerformanceChart,
      problemByYearChart,
      severityDistributionChart,
      totalStudents,
      totalSections,
      totalSubjects,
      failingStudents,
      completedSurveys,
      averageScore,
      assessmentCompletion,
      recentActivities,
      sections,
      subjects,
      quizzesGrades,
      surveyData,
      selectedYear,
      selectedSection,
      selectedSubject,
      selectedStartDate,
      selectedEndDate,
      today,
      loadingSurveyData,
      getTeacherId,
      hasPerformanceData,
      hasAssessmentData,
      hasActivity,
      hasSurveyData,
      fetchTeacherSectionsAndSubjects,
      handleYearChange,
      getFilterDisplay,
      fetchDashboardData,
      updatePerformanceChart,
      updateAssessmentTypeChart,
      updatePerformanceTrendChart,
      updateAssessmentTypePerformanceChart,
      formatDate,
      applyFilters,
      createProblemByYearChart,
      createSeverityDistributionChart,
      ref,
      onMounted,
      computed,
      watch,
      nextTick,
      AllSurveyStat,
      get useStore() {
        return useStore;
      },
      get Chart() {
        return Chart;
      },
      get axios() {
        return axios;
      },
      get moment() {
        return moment;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
