import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.for-each.js";
import "core-js/modules/es.iterator.reduce.js";
import "core-js/modules/es.iterator.some.js";
import { ref, onMounted, computed, watch, onUnmounted, nextTick } from 'vue';
import { useStore } from 'vuex';
import Chart from 'chart.js/auto';
import axios from 'axios';
import moment from 'moment';

// Create axios instance with default config
const api = axios.create({
  baseURL: 'http://localhost:8000/api'
});
export default {
  name: 'Dashboard',
  setup() {
    const store = useStore();
    const performanceChart = ref(null);
    const assessmentTypeChart = ref(null);
    const performanceTrendChart = ref(null);
    const sectionChart = ref(null);
    const selectedYear = ref('');
    const selectedSection = ref('');
    const selectedSubject = ref('');
    const dashboardContainer = ref(null);
    const isLoading = ref(false);
    const lastUpdate = ref(null);
    const autoRefreshInterval = ref(null);
    const sections = ref([]);
    const subjects = ref([]);

    // Computed properties
    const isCITHead = computed(() => store.getters.isCITHead);
    const token = computed(() => store.state.auth.token);

    // Get user name
    const userName = computed(() => {
      const user = store.state.auth.user;
      return user ? user.name || `${user.firstName || ''} ${user.lastName || ''}`.trim() || 'User' : 'User';
    });

    // Check if filters are active
    const hasActiveFilters = computed(() => {
      return Boolean(selectedYear.value || selectedSection.value || selectedSubject.value);
    });

    // Set auth token for all requests
    watch(token, newToken => {
      if (newToken) {
        api.defaults.headers.common['Authorization'] = `Bearer ${newToken}`;
      }
    }, {
      immediate: true
    });

    // Dashboard stats
    const totalStudents = ref(0);
    const totalTeachers = ref(0);
    const averageAttendance = ref(0);
    const averageScore = ref(0);
    const activeSections = ref(0);
    const data = ref(null);

    // Computed properties for data availability
    const hasAttendanceData = computed(() => {
      if (data.value && data.value.attendanceDistribution) {
        return data.value.attendanceDistribution.some(val => val > 0);
      }
      return false;
    });
    const hasScoreData = computed(() => {
      return data.value?.averageScore > 0;
    });
    const hasPerformanceData = computed(() => {
      // Check if we have any assessment or performance data
      if (data.value) {
        // Check for averageScore > 0
        if (typeof data.value.averageScore === 'number' && data.value.averageScore > 0) {
          return true;
        }

        // Check for performance trends
        if (Array.isArray(data.value.performanceTrends) && data.value.performanceTrends.length > 0) {
          return true;
        }
      }
      return false;
    });
    const hasAssessmentData = computed(() => {
      if (data.value) {
        // Check directly in assessmentTypes
        if (data.value.assessmentTypes) {
          const assessmentTypes = Object.values(data.value.assessmentTypes);
          return assessmentTypes.some(type => type.count > 0);
        }

        // Fallback to assessmentTypeDistribution
        if (data.value.assessmentTypeDistribution) {
          return true;
        }
      }
      return false;
    });
    const hasSectionData = computed(() => {
      return data.value?.sections?.length > 0;
    });

    // Fetch sections and subjects
    const fetchSectionsAndSubjects = async (year = '') => {
      try {
        const response = await api.get('/department/sections-subjects', {
          params: {
            year
          }
        });
        if (response.data) {
          // Extract unique sections and subjects
          sections.value = response.data.sections || [];
          subjects.value = response.data.subjects || [];
        }
      } catch (error) {
        console.error('Error fetching sections and subjects:', error);
        sections.value = [];
        subjects.value = [];
      }
    };
    const fetchDashboardData = async () => {
      try {
        isLoading.value = true;
        const response = await api.get('/dashboard/stats', {
          params: {
            year: selectedYear.value,
            section: selectedSection.value,
            subject: selectedSubject.value
          }
        });
        data.value = response.data;

        // Update stats
        totalStudents.value = data.value.totalStudents || 0;
        totalTeachers.value = data.value.totalTeachers || 0;
        averageAttendance.value = data.value.averageAttendance || 0;
        averageScore.value = data.value.averageScore || 0;
        activeSections.value = data.value.activeSections || 0;

        // Update charts with new data
        if (data.value) {
          updateCharts(data.value);
        }
        lastUpdate.value = new Date();
      } catch (error) {
        console.error('Failed to fetch dashboard data:', error);
        if (error.response) {
          console.error('Error response:', error.response.data);
        }
      } finally {
        isLoading.value = false;
      }
    };
    const updateCharts = data => {
      updatePerformanceChart(data);
      updateAssessmentTypeChart(data);
      updatePerformanceTrendChart(data);
      updateSectionChart(data);
    };

    // Initialize dummy data for charts when no data is available
    const initDummyData = () => {
      updatePerformanceChart({
        attendanceDistribution: [0, 0, 0]
      });
      updateAssessmentTypeChart({
        assessmentData: [{
          type: 'Quiz',
          averageScore: 0
        }, {
          type: 'Activity',
          averageScore: 0
        }, {
          type: 'Performance Task',
          averageScore: 0
        }]
      });
      updatePerformanceTrendChart([]);
      updateSectionChart({
        sections: []
      });
    };

    // Format date for display
    const formatDate = date => {
      if (!date) return '';
      return moment(date).format('MMMM D, YYYY h:mm A');
    };

    // Handle year change
    const handleYearChange = async () => {
      selectedSection.value = '';
      selectedSubject.value = '';
      if (selectedYear.value) {
        await fetchSectionsAndSubjects(selectedYear.value);
      } else {
        sections.value = [];
        subjects.value = [];
      }
      await fetchDashboardData();
    };

    // Handle filter change
    const handleFilterChange = () => {
      fetchDashboardData();
    };

    // Clear all filters
    const clearFilters = () => {
      selectedYear.value = '';
      selectedSection.value = '';
      selectedSubject.value = '';
      sections.value = [];
      subjects.value = [];
      fetchDashboardData();
    };

    // Get filter display text
    const getFilterDisplay = () => {
      const filters = [];
      if (selectedYear.value) filters.push(selectedYear.value + ' Year');
      if (selectedSection.value) filters.push(selectedSection.value);
      if (selectedSubject.value) filters.push(selectedSubject.value);
      return filters.length > 0 ? filters.join(' - ') : 'Filter View';
    };

    // Update charts with data
    const updatePerformanceChart = data => {
      if (performanceChart.value) {
        const ctx = performanceChart.value.getContext('2d');

        // Dispose of existing chart if it exists
        if (ctx.chart) {
          ctx.chart.destroy();
        }

        // Attendance status distribution data
        const attendanceLabels = ['Present', 'Late', 'Absent'];
        const attendanceData = data.attendanceDistribution || [0, 0, 0];

        // Create chart
        ctx.chart = new Chart(ctx, {
          type: 'doughnut',
          data: {
            labels: attendanceLabels,
            datasets: [{
              data: attendanceData,
              backgroundColor: ['#10b981', '#f59e0b', '#ef4444'],
              borderWidth: 0,
              hoverOffset: 4
            }]
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            cutout: '70%',
            plugins: {
              legend: {
                position: 'bottom',
                labels: {
                  usePointStyle: true,
                  padding: 15,
                  font: {
                    size: 12
                  }
                }
              },
              tooltip: {
                callbacks: {
                  label: function (context) {
                    const label = context.label || '';
                    const value = context.raw || 0;
                    const total = context.chart.data.datasets[0].data.reduce((a, b) => a + b, 0);
                    const percentage = total > 0 ? Math.round(value / total * 100) : 0;
                    return `${label}: ${value} (${percentage}%)`;
                  }
                }
              }
            }
          }
        });
      }
    };
    const updateAssessmentTypeChart = data => {
      if (assessmentTypeChart.value) {
        const ctx = assessmentTypeChart.value.getContext('2d');

        // Dispose of existing chart if it exists
        if (ctx.chart) {
          ctx.chart.destroy();
        }

        // Assessment types data
        const assessmentData = data.assessmentTypes || [];
        const labels = [];
        const values = [];
        const colors = ['#3b82f6', '#10b981', '#f59e0b', '#8b5cf6', '#ec4899', '#6366f1'];

        // Extract assessment types data
        if (Array.isArray(assessmentData)) {
          // If array format
          assessmentData.forEach((item, index) => {
            labels.push(item.type);
            values.push(item.averageScore);
          });
        } else {
          // If object format
          Object.entries(assessmentData).forEach(([type, data], index) => {
            labels.push(type);
            values.push(data.averageScore || 0);
          });
        }

        // Create chart
        ctx.chart = new Chart(ctx, {
          type: 'bar',
          data: {
            labels: labels,
            datasets: [{
              label: 'Average Score (%)',
              data: values,
              backgroundColor: colors,
              borderWidth: 0,
              borderRadius: 6,
              maxBarThickness: 50
            }]
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
              legend: {
                display: false
              },
              tooltip: {
                callbacks: {
                  label: function (context) {
                    return `Average Score: ${context.raw}%`;
                  }
                }
              }
            },
            scales: {
              y: {
                beginAtZero: true,
                max: 100,
                ticks: {
                  callback: function (value) {
                    return value + '%';
                  }
                }
              }
            }
          }
        });
      }
    };
    const updatePerformanceTrendChart = data => {
      if (performanceTrendChart.value) {
        const ctx = performanceTrendChart.value.getContext('2d');

        // Dispose of existing chart if it exists
        if (ctx.chart) {
          ctx.chart.destroy();
        }

        // Performance trends data
        const performanceTrends = data.performanceTrends || [];
        const labels = [];
        const scores = [];

        // Extract performance trends data
        performanceTrends.forEach(item => {
          // Format date if available
          const date = item.date ? moment(item.date).format('MMM D') : '';
          // Use assessment title or date as label
          labels.push(item.title ? `${item.title} (${date})` : date);
          scores.push(item.averageScore || 0);
        });

        // Create chart
        ctx.chart = new Chart(ctx, {
          type: 'line',
          data: {
            labels: labels,
            datasets: [{
              label: 'Average Score',
              data: scores,
              backgroundColor: 'rgba(59, 130, 246, 0.1)',
              borderColor: '#3b82f6',
              borderWidth: 2,
              tension: 0.4,
              fill: true,
              pointBackgroundColor: '#3b82f6',
              pointRadius: 4,
              pointHoverRadius: 6
            }]
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
              legend: {
                display: false
              },
              tooltip: {
                callbacks: {
                  label: function (context) {
                    return `Average Score: ${context.raw}%`;
                  }
                }
              }
            },
            scales: {
              y: {
                beginAtZero: true,
                max: 100,
                ticks: {
                  callback: function (value) {
                    return value + '%';
                  }
                }
              },
              x: {
                ticks: {
                  maxRotation: 45,
                  minRotation: 45
                }
              }
            }
          }
        });
      }
    };
    const updateSectionChart = data => {
      if (sectionChart.value) {
        const ctx = sectionChart.value.getContext('2d');

        // Dispose of existing chart if it exists
        if (ctx.chart) {
          ctx.chart.destroy();
        }

        // Section performance data
        const sectionData = data.sections || [];
        const labels = [];
        const performanceData = [];
        const attendanceData = [];

        // Extract section performance data
        sectionData.forEach(section => {
          labels.push(section.name);
          performanceData.push(section.averageScore || 0);
          attendanceData.push(section.attendanceRate || 0);
        });

        // Create chart
        ctx.chart = new Chart(ctx, {
          type: 'bar',
          data: {
            labels: labels,
            datasets: [{
              label: 'Average Score',
              data: performanceData,
              backgroundColor: '#3b82f6',
              borderWidth: 0,
              borderRadius: 6,
              maxBarThickness: 30
            }, {
              label: 'Attendance Rate',
              data: attendanceData,
              backgroundColor: '#10b981',
              borderWidth: 0,
              borderRadius: 6,
              maxBarThickness: 30
            }]
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
              legend: {
                position: 'bottom',
                labels: {
                  usePointStyle: true,
                  padding: 15,
                  font: {
                    size: 12
                  }
                }
              },
              tooltip: {
                callbacks: {
                  label: function (context) {
                    const label = context.dataset.label || '';
                    const value = context.raw || 0;
                    return `${label}: ${value}%`;
                  }
                }
              }
            },
            scales: {
              y: {
                beginAtZero: true,
                max: 100,
                ticks: {
                  callback: function (value) {
                    return value + '%';
                  }
                }
              }
            }
          }
        });
      }
    };
    const refreshDashboard = () => {
      fetchDashboardData();
    };
    const scrollToSection = sectionId => {
      const section = document.getElementById(sectionId);
      if (section) {
        section.scrollIntoView({
          behavior: 'smooth'
        });
      }
    };

    // Auto-refresh logic
    const setupAutoRefresh = () => {
      // Clear any existing interval
      if (autoRefreshInterval.value) {
        clearInterval(autoRefreshInterval.value);
      }

      // Set new interval (every 5 minutes)
      autoRefreshInterval.value = setInterval(() => {
        fetchDashboardData();
      }, 5 * 60 * 1000);
    };

    // Fetch data on component mount
    onMounted(async () => {
      try {
        // Initialize with dummy data while loading
        nextTick(() => {
          initDummyData();
        });

        // Fetch sections and subjects
        if (selectedYear.value) {
          await fetchSectionsAndSubjects(selectedYear.value);
        }

        // Fetch dashboard data
        await fetchDashboardData();
        setupAutoRefresh();
      } catch (error) {
        console.error('Error initializing dashboard:', error);
      }
    });

    // Clean up on component unmount
    onUnmounted(() => {
      if (autoRefreshInterval.value) {
        clearInterval(autoRefreshInterval.value);
      }
    });
    return {
      performanceChart,
      assessmentTypeChart,
      performanceTrendChart,
      sectionChart,
      selectedYear,
      selectedSection,
      selectedSubject,
      sections,
      subjects,
      isCITHead,
      userName,
      hasActiveFilters,
      totalStudents,
      totalTeachers,
      averageAttendance,
      averageScore,
      activeSections,
      hasAttendanceData,
      hasScoreData,
      hasPerformanceData,
      hasAssessmentData,
      hasSectionData,
      dashboardContainer,
      isLoading,
      lastUpdate,
      formatDate,
      scrollToSection,
      handleYearChange,
      handleFilterChange,
      clearFilters,
      getFilterDisplay,
      refreshDashboard
    };
  }
};
