          console.error('Error fetching failing students:', error);
          failingStudents.value = 0;
          completedSurveys.value = 0;
          surveyData.value = [];
        }

        // Log survey data
        console.log('Survey Data:', surveyData.value);

        // Only update survey average chart
        nextTick(() => {
          createSurveyAverageChart();
        });
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
        if (error.response) {
          console.error('Error response:', error.response.data);
        }

        // Reset data on error
        totalStudents.value = 0;
        totalSections.value = 0;
        totalSubjects.value = 0;
        failingStudents.value = 0;
        completedSurveys.value = 0;
        surveyData.value = [];

        // Update just survey average chart with empty data
        nextTick(() => {
          createSurveyAverageChart();
        });
      }
    };
    const createSurveyAverageChart = () => {
      if (!surveyAverageChart.value) return;
      const ctx = surveyAverageChart.value.getContext('2d');
      if (!ctx) return;
      const existingChart = Chart.getChart(ctx);
      if (existingChart) existingChart.destroy();

      // Process survey data to calculate average scores for each question
      if (!surveyData.value || surveyData.value.length === 0) {
        // Display empty chart if no data
        new Chart(ctx, {
          type: 'bar',
          data: {
            labels: ['No survey data available'],
            datasets: [{
              data: [0],
              backgroundColor: 'rgba(156, 163, 175, 0.6)',
              borderWidth: 1
            }]
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
              legend: {
                display: false
              },
              tooltip: {
                enabled: false
              }
            },
            scales: {
              y: {
                beginAtZero: true,
                max: 5,
                title: {
                  display: true,
                  text: 'Average Score'
                }
              }
            }
          }
        });
        return;
      }

      // Extract questions and calculate average scores
      const questionLabels = [];
      const questionScores = [];

      // Collect all unique questions
      const allQuestions = new Set();
      surveyData.value.forEach(survey => {
        if (survey.responses && Array.isArray(survey.responses)) {
          survey.responses.forEach(response => {
            if (response.question) {
              allQuestions.add(response.question);
            }
          });
        }
      });

      // Sort questions alphabetically for consistent display
      const sortedQuestions = [...allQuestions].sort();

      // Calculate average score for each question
      sortedQuestions.forEach(question => {
        let totalScore = 0;
        let count = 0;
        surveyData.value.forEach(survey => {
          if (survey.responses && Array.isArray(survey.responses)) {
            survey.responses.forEach(response => {
              if (response.question === question && response.answer !== undefined && !isNaN(response.answer)) {
                totalScore += Number(response.answer);
                count++;
              }
            });
          }
        });
        const averageScore = count > 0 ? totalScore / count : 0;
        questionLabels.push(question.length > 30 ? question.substring(0, 30) + '...' : question);
        questionScores.push(parseFloat(averageScore.toFixed(2)));
      });
      new Chart(ctx, {
        type: 'bar',
        data: {
          labels: questionLabels,
          datasets: [{
            label: 'Average Score',
            data: questionScores,
            backgroundColor: 'rgba(59, 130, 246, 0.8)',
            borderColor: 'rgba(59, 130, 246, 1)',
            borderWidth: 1,
            borderRadius: 5
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          plugins: {
            legend: {
              display: false
            },
            tooltip: {
              callbacks: {
                title: tooltipItems => {
                  // Show full question text in tooltip
                  const index = tooltipItems[0].dataIndex;
                  return sortedQuestions[index];
                },
                label: context => {
                  return `Average Score: ${context.raw}`;
                }
              }
            }
          },
          scales: {
            y: {
              beginAtZero: true,
              max: 5,
              title: {
                display: true,
                text: 'Average Score (1-5)'
              }
            },
            x: {
              ticks: {
                maxRotation: 45,
                minRotation: 45
              }
            }
          }
        }
      });
    };
    const formatDate = date => {
      return moment(date).format('MMM D, YYYY');
    };
    const applyFilters = async () => {
      // Save selected values to localStorage
      if (selectedYear.value) {
        localStorage.setItem('selectedYear', selectedYear.value);
      } else {
        localStorage.removeItem('selectedYear');
      }
      if (selectedSection.value) {
        localStorage.setItem('selectedSection', selectedSection.value);
      } else {
        localStorage.removeItem('selectedSection');
      }
      if (selectedSubject.value) {
        localStorage.setItem('selectedSubject', selectedSubject.value);
      } else {
        localStorage.removeItem('selectedSubject');
      }
      await fetchDashboardData();
    };

    // Watch for filter changes
    watch(selectedYear, handleYearChange);
    watch([selectedSection, selectedSubject], applyFilters);
    watch([selectedStartDate, selectedEndDate], () => {
      if (selectedStartDate.value && selectedEndDate.value) {
        // Validate date range
        const start = moment(selectedStartDate.value);
        const end = moment(selectedEndDate.value);
        if (end.isBefore(start)) {
          selectedEndDate.value = selectedStartDate.value;
        }
        fetchDashboardData();
      }
    });

    // Watch surveyData for changes to update charts
    watch(surveyData, () => {
      loadingSurveyData.value = true;

      // Use nextTick to ensure DOM is updated
      nextTick(() => {
        try {
          createProblemByYearChart();
          createSeverityDistributionChart();
        } catch (error) {
          console.error('Error creating survey charts:', error);
        } finally {
          loadingSurveyData.value = false;
        }
      });
    }, {
      deep: true
    });
    onMounted(async () => {
      console.log('SSP Dashboard mounted');
      await fetchDashboardData();

      // Initialize charts
      createSurveyAverageChart();

      // Set up watchers for filters
      watch([selectedYear, selectedSection, selectedSubject, selectedStartDate, selectedEndDate], async () => {
        console.log('Filters changed, refreshing data');
        await fetchDashboardData();
      });
    });

    // Create chart for problem types by year level
    const createProblemByYearChart = () => {
      if (!problemByYearChart.value) return;
      const ctx = problemByYearChart.value.getContext('2d');
      if (!ctx) return;
      const existingChart = Chart.getChart(ctx);
      if (existingChart) existingChart.destroy();
      if (!surveyData.value || surveyData.value.length === 0) {
        // If no data, show empty chart
        new Chart(ctx, {
          type: 'bar',
          data: {
            labels: ['1st Year', '2nd Year', '3rd Year', '4th Year'],
            datasets: [{
              label: 'Academic',
              data: [0, 0, 0, 0],
              backgroundColor: 'rgba(54, 162, 235, 0.7)'
            }, {
              label: 'Financial',
              data: [0, 0, 0, 0],
              backgroundColor: 'rgba(255, 99, 132, 0.7)'
            }, {
              label: 'Personal',
              data: [0, 0, 0, 0],
              backgroundColor: 'rgba(255, 206, 86, 0.7)'
            }, {
              label: 'Health',
              data: [0, 0, 0, 0],
              backgroundColor: 'rgba(75, 192, 192, 0.7)'
            }, {
              label: 'Social',
              data: [0, 0, 0, 0],
              backgroundColor: 'rgba(153, 102, 255, 0.7)'
            }]
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            scales: {
              x: {
                stacked: true
              },
              y: {
                stacked: true,
                beginAtZero: true,
                title: {
                  display: true,
                  text: 'Number of Problems'
                }
              }
            }
          }
        });
        return;
      }

      // Process data - group problem types by year
      const problemTypesByYear = {
        '1st': {},
        '2nd': {},
        '3rd': {},
        '4th': {},
        '5th': {},
        '6th': {}
      };

      // Collect all unique problem types
      const allProblemTypes = new Set();

      // Process all surveys and their stats
      surveyData.value.forEach(survey => {
        const yearLevel = survey.year || 'Unknown';
        if (!problemTypesByYear[yearLevel]) {
          problemTypesByYear[yearLevel] = {};
        }
        if (survey.surveyStats && Array.isArray(survey.surveyStats)) {
          survey.surveyStats.forEach(stat => {
            const problemType = stat.type;
            allProblemTypes.add(problemType);
            if (!problemTypesByYear[yearLevel][problemType]) {
              problemTypesByYear[yearLevel][problemType] = 0;
            }
            problemTypesByYear[yearLevel][problemType]++;
          });
        }
      });

      // Convert to chart data format
      const problemTypesArray = Array.from(allProblemTypes);
      const yearsArray = Object.keys(problemTypesByYear).filter(year => year !== 'Unknown' && Object.keys(problemTypesByYear[year]).length > 0);

      // Create datasets for each problem type
      const datasets = problemTypesArray.map((problemType, index) => {
        const colors = ['rgba(54, 162, 235, 0.7)',
        // Blue
        'rgba(255, 99, 132, 0.7)',
        // Red
        'rgba(255, 206, 86, 0.7)',
        // Yellow
        'rgba(75, 192, 192, 0.7)',
        // Green
        'rgba(153, 102, 255, 0.7)',
        // Purple
        'rgba(255, 159, 64, 0.7)' // Orange
        ];
        return {
          label: problemType,
          data: yearsArray.map(year => problemTypesByYear[year][problemType] || 0),
          backgroundColor: colors[index % colors.length]
        };
      });

