    const updatePerformanceChart = data => {
      // Process attendance data
      const attendanceData = data.attendanceStats || {
        present: 0,
        absent: 0,
        late: 0,
        excused: 0
      };

      // Calculate total records
      const totalRecords = (attendanceData.present || 0) + (attendanceData.absent || 0) + (attendanceData.late || 0) + (attendanceData.excused || 0);
      totalAttendanceRecords.value = totalRecords;

      // Calculate attendance rate (present + excused) / total * 100
      if (totalRecords > 0) {
        attendanceRate.value = (attendanceData.present + attendanceData.excused) / totalRecords * 100;
      } else {
        attendanceRate.value = 0;
      }

      // Prepare chart data
      const chartData = {
        labels: ['Present', 'Absent', 'Late', 'Excused'],
        datasets: [{
          data: [attendanceData.present || 0, attendanceData.absent || 0, attendanceData.late || 0, attendanceData.excused || 0],
