          // Green for present
          'rgba(231, 76, 60, 0.7)',
          // Red for absent
          'rgba(241, 196, 15, 0.7)',
          // Yellow for late
          'rgba(52, 152, 219, 0.7)' // Blue for excused
          ],
          borderColor: ['rgba(39, 174, 96, 1)', 'rgba(231, 76, 60, 1)', 'rgba(241, 196, 15, 1)', 'rgba(52, 152, 219, 1)'],
          borderWidth: 1,
          hoverOffset: 4
        }]
      };

      // Chart options
      const options = {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          legend: {
            position: 'right',
            labels: {
              font: {
                family: "'Poppins', sans-serif",
                size: 12
              },
              padding: 20
            }
