          tooltip: {
            callbacks: {
              label: function (context) {
                const label = context.label || '';
                const value = context.raw || 0;
                const percentage = totalRecords > 0 ? (value / totalRecords * 100).toFixed(1) + '%' : '0%';
                return `${label}: ${value} (${percentage})`;
              }
            }
          },
          datalabels: {
            display: totalRecords > 0,
            formatter: (value, ctx) => {
              if (value === 0) return '';
              return totalRecords > 0 ? (value / totalRecords * 100).toFixed(0) + '%' : '';
            },
            color: '#fff',
            font: {
              weight: 'bold'
            }
          }
        }
      };

      // Check if chart instance exists and destroy it
      if (performanceChart.value) {
        performanceChart.value.destroy();
      }

      // Get canvas element
      const ctx = document.getElementById('performanceDistChart');
