          assessmentTypes: assessmentTypes,
          averageScores: {},
          totalCounts: {},
          passRates: {}
        };
        assessmentTypes.forEach(type => {
          assessmentData.averageScores[type] = 0;
          assessmentData.totalCounts[type] = 0;
          assessmentData.passRates[type] = 0;
        });
      } else {
        // Process actual data
        assessmentData = {
          assessmentTypes: assessmentTypes,
          averageScores: {},
          totalCounts: {},
          passRates: {}
        };

        // Calculate average scores and pass rates per assessment type
        assessmentTypes.forEach(type => {
          const typeAssessments = data.assessmentData.filter(a => a.type === type);
          const totalCount = typeAssessments.length;
          assessmentData.totalCounts[type] = totalCount;
          if (totalCount > 0) {
            // Calculate average score
            const totalScore = typeAssessments.reduce((sum, a) => sum + (parseFloat(a.averageScore) || 0), 0);
            assessmentData.averageScores[type] = (totalScore / totalCount).toFixed(1);

            // Calculate pass rate
            const passingCount = typeAssessments.filter(a => (parseFloat(a.averageScore) || 0) >= 70).length;
            assessmentData.passRates[type] = (passingCount / totalCount * 100).toFixed(1);
