            },
            tooltip: {
              callbacks: {
                label: context => {
                  const label = context.dataset.label || '';
                  const value = context.raw || 0;
                  return `${label}: ${value}%`;
                },
                afterLabel: context => {
                  const type = assessmentTypes[context.dataIndex];
                  const count = assessmentData.totalCounts[type] || 0;
                  return `Total ${type}s: ${count}`;
                }
              },
              titleFont: {
                size: 14,
                weight: 'bold'
              },
              bodyFont: {
                size: 13
              },
              padding: 12
