      // Add insightful summary
      summaryDiv.innerHTML = `
        <div class="assessment-insight">
          <div class="insight-title">Performance Insights:</div>
          <div class="insight-item">
            <span class="insight-label">Highest performing assessment type:</span>
            <span class="insight-value">${highestType} (${highestScore}%)</span>
          </div>
          <div class="insight-item">
            <span class="insight-label">Total assessments analyzed:</span>
            <span class="insight-value">${assessmentTypes.reduce((sum, type) => sum + (assessmentData.totalCounts[type] || 0), 0)}</span>
          </div>
        </div>
      `;
    };
    const updatePerformanceTrendChart = data => {
      if (!performanceTrendChart.value) {
        console.warn('Performance trend chart reference not found');
        return;
      }
      const ctx = performanceTrendChart.value.getContext('2d');
      if (!ctx) {
        console.warn('Could not get 2d context for performance trend chart');
        return;
      }
      const existingChart = Chart.getChart(ctx);
      if (existingChart) {
        existingChart.destroy();
      }

      // Ensure data is valid
      const validData = Array.isArray(data) ? data : [];
      console.log('Creating performance trend chart with data:', validData);
      new Chart(ctx, {
        type: 'line',
        data: {
          labels: validData.map(d => moment(d.date).format('MMM D, YYYY')),
