            tooltip: {
              callbacks: {
                label: function (context) {
                  const label = context.label || '';
                  const value = context.raw || 0;
                  const total = context.chart.data.datasets[0].data.reduce((a, b) => a + b, 0);
                  const percentage = total > 0 ? Math.round(value / total * 100) : 0;
                  return `${label}: ${value} (${percentage}%)`;
                }
              }
            }
          }
        }
      });
    };
    const updateAssessmentTypeChart = data => {
      if (!assessmentTypeChart.value) return;
      const ctx = assessmentTypeChart.value.getContext('2d');
      if (!ctx) return;

      // Dispose of existing chart if it exists
      const existingChart = Chart.getChart(ctx);
      if (existingChart) existingChart.destroy();

      // Assessment types data
      const assessmentData = data.assessmentTypes || [];
      const labels = [];
      const values = [];
      const colors = ['#3b82f6', '#10b981', '#f59e0b', '#8b5cf6', '#ec4899', '#6366f1'];
      try {
        // Extract assessment types data
        if (Array.isArray(assessmentData)) {
          // If array format
          assessmentData.forEach((item, index) => {
            if (item && typeof item === 'object') {
              labels.push(item.type || `Type ${index + 1}`);
              values.push(parseFloat(item.averageScore) || 0);
            }
          });
        } else if (typeof assessmentData === 'object' && assessmentData !== null) {
          // If object format
          Object.entries(assessmentData).forEach(([type, data], index) => {
            labels.push(type);
            values.push(parseFloat(data.averageScore) || 0);
          });
        }

        // If no data was extracted, use default types
        if (labels.length === 0) {
          labels.push('Quiz', 'Activity', 'Performance Task');
          values.push(0, 0, 0);
        }
      } catch (error) {
        console.error('Error processing assessment data:', error);
        // Fallback to default labels and values
        labels.push('Quiz', 'Activity', 'Performance Task');
        values.push(0, 0, 0);
      }

      // Create chart
      new Chart(ctx, {
        type: 'bar',
        data: {
          labels: labels,
          datasets: [{
            label: 'Average Score (%)',
            data: values,
            backgroundColor: colors.slice(0, labels.length),
            borderWidth: 0,
            borderRadius: 6,
            maxBarThickness: 50
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          plugins: {
            legend: {
              display: false
            },
            tooltip: {
              callbacks: {
                label: function (context) {
                  return `Average Score: ${context.raw}%`;
                }
              }
            }
          },
          scales: {
            y: {
              beginAtZero: true,
              max: 100,
              ticks: {
                callback: function (value) {
                  return value + '%';
                }
              }
            }
          }
        }
      });
    };
    const updatePerformanceTrendChart = data => {
      if (!performanceTrendChart.value) return;
      const ctx = performanceTrendChart.value.getContext('2d');
      if (!ctx) return;

      // Properly destroy any existing chart
      const existingChart = Chart.getChart(ctx);
      if (existingChart) existingChart.destroy();
      let trends = [];
      if (data.performanceTrends && Array.isArray(data.performanceTrends)) {
        trends = data.performanceTrends.map(trend => ({
          date: new Date(trend.date),
          value: parseFloat(trend.averageScore || trend.score) || 0,
          name: trend.title || trend.name || 'Assessment'
        })).sort((a, b) => a.date - b.date);
      }

      // Create new chart after destroying the old one
      new Chart(ctx, {
        type: 'line',
        data: {
          labels: trends.map(trend => moment(trend.date).format('MMM D, YYYY')),
          datasets: [{
            label: 'Average Score',
            data: trends.map(trend => trend.value),
            borderColor: 'rgb(59, 130, 246)',
            backgroundColor: 'rgba(59, 130, 246, 0.1)',
            borderWidth: 2,
            tension: 0.4,
            fill: true,
            pointRadius: 4,
            pointBackgroundColor: 'rgb(59, 130, 246)',
            pointHoverRadius: 6
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          plugins: {
            legend: {
              display: true,
              position: 'top'
            },
            tooltip: {
              callbacks: {
                title: tooltipItems => {
                  if (!tooltipItems.length || !trends[tooltipItems[0].dataIndex]) return '';
                  const trend = trends[tooltipItems[0].dataIndex];
                  return `${moment(trend.date).format('MMM D, YYYY')} - ${trend.name}`;
                },
                label: context => {
                  return `Score: ${context.raw.toFixed(1)}%`;
                }
              }
            }
          },
          scales: {
            y: {
              beginAtZero: true,
              max: 100,
              title: {
                display: true,
                text: 'Average Score (%)'
              }
            },
            x: {
              ticks: {
                maxRotation: 45,
                minRotation: 45
              }
            }
          }
        }
      });
    };
    const updateAssessmentTypePerformanceChart = data => {
      if (!assessmentTypePerformanceChart.value) return;
      const ctx = assessmentTypePerformanceChart.value.getContext('2d');
      if (!ctx) return;
      const existingChart = Chart.getChart(ctx);
      if (existingChart) existingChart.destroy();

      // Process the performance trends data by assessment type
      const trendsByType = {
        Quiz: [],
        Activity: [],
        'Performance Task': []
      };

      // Process performance trends data
      if (Array.isArray(data.performanceTrends)) {
        data.performanceTrends.forEach(trend => {
          const type = trend.type || trend.title;
          if (type in trendsByType) {
            trendsByType[type].push({
              date: new Date(trend.date),
              score: parseFloat(trend.averageScore || trend.score) || 0
            });
          }
        });
      }

      // Sort data points by date for each type
      Object.keys(trendsByType).forEach(type => {
        trendsByType[type].sort((a, b) => a.date - b.date);
      });

      // Get unique dates across all types
      const allDates = [...new Set(Object.values(trendsByType).flat().map(item => item.date))].sort((a, b) => a - b);

      // Create datasets
      const datasets = Object.entries(trendsByType).map(([type, data], index) => {
        const colors = ['rgb(52, 211, 153)',
        // Green for Quiz
        'rgb(59, 130, 246)',
        // Blue for Activity
        'rgb(251, 191, 36)' // Yellow for Performance Task
        ];
        const color = colors[index];
        return {
          label: type,
          data: allDates.map(date => {
            const point = data.find(d => d.date.getTime() === date.getTime());
            return point ? point.score : null;
          }),
          borderColor: color,
          backgroundColor: color.replace('rgb', 'rgba').replace(')', ', 0.1)'),
          fill: true,
          tension: 0.4,
          pointRadius: 4,
          pointHoverRadius: 6,
          spanGaps: true // This will connect points even if there are null values
        };
      });
      new Chart(ctx, {
        type: 'line',
        data: {
          labels: allDates.map(date => moment(date).format('MMM D, YYYY')),
          datasets
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          interaction: {
            intersect: false,
            mode: 'index'
          },
          plugins: {
            legend: {
              position: 'top'
            },
            tooltip: {
              callbacks: {
                label: context => {
                  const value = context.raw !== null ? context.raw.toFixed(1) : 'N/A';
                  return `${context.dataset.label}: ${value}%`;
                }
              }
            }
          },
          scales: {
            y: {
              beginAtZero: true,
              max: 100,
              title: {
                display: true,
                text: 'Score (%)'
              }
            },
            x: {
              ticks: {
                maxRotation: 45,
                minRotation: 45
              }
            }
          }
        }
      });
    };

    // Update the refreshDashboard function to properly handle chart destruction
    const refreshDashboard = async () => {
      try {
        isLoading.value = true;

        // Destroy existing charts before fetching new data
        initEmptyCharts();

        // Wait a tick to ensure DOM updates
        await nextTick();

        // Fetch new data
        await fetchDashboardData();

        // Update the last refresh time
        lastUpdate.value = new Date();
      } catch (error) {
        console.error('Error refreshing dashboard:', error);
      } finally {
        isLoading.value = false;
      }
    };
    const scrollToSection = sectionId => {
      const section = document.getElementById(sectionId);
      if (section) {
        section.scrollIntoView({
          behavior: 'smooth'
        });
      }
    };

    // Restore auto-refresh in onMounted function
    onMounted(async () => {
      try {
        // Make sure DOM is rendered before initializing charts
        await nextTick();

        // Initialize with empty data while waiting for API
        initEmptyCharts();

        // Set up API interceptor to handle 404 errors gracefully
        api.interceptors.response.use(response => response, error => {
          if (error.response && error.response.status === 404) {
            console.warn(`API endpoint not found: ${error.config.url}`);
          }
          return Promise.reject(error);
        });

        // Set auth token for API requests
        if (store.state.auth.token) {
          api.defaults.headers.common['Authorization'] = `Bearer ${store.state.auth.token}`;
        }

        // Fetch available years and sections with data
        await fetchAvailableFilters();

        // Fetch dashboard data
        await fetchDashboardData();

        // Set up auto-refresh
        if (autoRefreshInterval.value) {
          clearInterval(autoRefreshInterval.value);
        }

        // Refresh every 5 minutes, but only if tab is visible
        autoRefreshInterval.value = setInterval(() => {
          if (document.visibilityState === 'visible') {
            refreshDashboard();
          }
        }, 5 * 60 * 1000); // 5 minutes
      } catch (error) {
        console.error('Error initializing dashboard:', error);
        // Make sure we still have empty charts if data loading fails
        await nextTick();
        initEmptyCharts();
      }
    });

    // Clean up on component unmount
    onUnmounted(() => {
      if (autoRefreshInterval.value) {
        clearInterval(autoRefreshInterval.value);
      }
    });

    // Add watch for date changes
    watch([selectedStartDate, selectedEndDate], () => {
      if (selectedStartDate.value && selectedEndDate.value) {
        // Validate date range
        const start = moment(selectedStartDate.value);
        const end = moment(selectedEndDate.value);
        if (end.isBefore(start)) {
          selectedEndDate.value = selectedStartDate.value;
        }
      }
    });

    // Add fetchSubjects function
    const fetchSubjects = async (year = '', section = '') => {
      try {
        // Get subjects based on year and section (if provided)
        const params = {};
        if (year) params.year = year;
        if (section) params.section = section;
        console.log('Fetching subjects with params:', params);

        // Try teacher class records first
        try {
          const response = await api.get('/teacher-class-records/subjects', {
            params
          });
          if (response.data && Array.isArray(response.data) && response.data.length > 0) {
            subjects.value = response.data.sort();
            console.log('Fetched subjects:', subjects.value);
            return;
          }
        } catch (error) {
          console.log('Failed to fetch subjects from teacher class records:', error.message);
        }

        // Fall back to assessments endpoint
        try {
          const response = await api.get('/assessments/subjects', {
            params
          });
          if (response.data && Array.isArray(response.data) && response.data.length > 0) {
            subjects.value = response.data.sort();
            console.log('Fetched subjects from assessments:', subjects.value);
            return;
          }
        } catch (error) {
          console.log('Failed to fetch subjects from assessments:', error.message);
        }

        // Fall back to manual extraction from assessments
        try {
          const response = await api.get('/assessments', {
            params
          });
          if (response.data && Array.isArray(response.data)) {
            const uniqueSubjects = [...new Set(response.data.map(a => a.subject))].filter(Boolean);
            if (uniqueSubjects.length > 0) {
              subjects.value = uniqueSubjects.sort();
              console.log('Extracted subjects from assessments:', subjects.value);
              return;
            }
          }
        } catch (error) {
          console.log('Failed to extract subjects from assessments:', error.message);
        }

        // Try to extract from attendance records
        try {
          const attendanceParams = {
            ...params
          };
          if (!attendanceParams.startDate) {
            // Default to last 90 days for a wider range
            attendanceParams.startDate = moment().subtract(90, 'days').format('YYYY-MM-DD');
          }
          if (!attendanceParams.endDate) {
            attendanceParams.endDate = moment().format('YYYY-MM-DD');
          }
          const response = await api.get('/attendance', {
            params: attendanceParams
          });
          if (response.data && Array.isArray(response.data)) {
            const uniqueSubjects = [...new Set(response.data.map(a => a.subject))].filter(Boolean);
            if (uniqueSubjects.length > 0) {
              subjects.value = uniqueSubjects.sort();
              console.log('Extracted subjects from attendance:', subjects.value);
              return;
            }
          }
        } catch (error) {
          console.log('Failed to extract subjects from attendance:', error.message);
        }

        // If all else fails, reset subjects
        subjects.value = [];
      } catch (error) {
        console.error('Error fetching subjects:', error);
        subjects.value = [];
      }
    };
    return {
      performanceChart,
      assessmentTypeChart,
      performanceTrendChart,
      assessmentTypePerformanceChart,
      selectedYear,
      selectedSection,
      selectedSubject,
      sections,
      subjects,
      availableYears,
      isCITHead,
      userName,
      hasActiveFilters,
      totalStudents,
      totalTeachers,
      averageAttendance,
      averageScore,
      activeSections,
      hasAttendanceData,
      hasScoreData,
      hasPerformanceData,
      hasAssessmentData,
      hasSectionData,
      dashboardContainer,
      isLoading,
      lastUpdate,
      formatDate,
      scrollToSection,
      handleYearChange,
      handleSectionChange,
      handleSubjectChange,
      clearFilters,
      getFilterDisplay,
      refreshDashboard,
      fetchAvailableFilters,
      selectedStartDate,
      selectedEndDate,
      today
    };
  }
};
